import React, { useState } from 'react'

const Main = () => {

    const[contador, setContador] = useState(0);

    return(
        <main className="main">
            Main Content
                {`Main content ${contador}`}
                <button
                onClick={()=> setContador( contador + 1)}
                >
                    Click me Alicia!
                </button>
                <button
                onClick={()=> setContador( contador - 1)}
                >
                    Not so fast!
                </button>
                <button
                onClick={()=> setContador(0)}
                >
                    Try Again!
                </button>
                
        </main>
    )
 }

 this.state = {
    contador: 0,
    user: ''
}
this.setState({
    contador:1
})
